import React, { useState, useEffect } from 'react';
import {NavLink} from 'react-router-dom';
import'./style.css';

const List=()=>{
  return(
    <nav className="navbar">
    <ul className="navbarMenu">
      <li><NavLink to="/">Home</NavLink></li>
      <li><NavLink to="/bollywood">Bollywood</NavLink></li>
      <li><NavLink to="/technology">Technology</NavLink></li>
      <li><NavLink to="/sport">Sport</NavLink></li>
      <li><NavLink to="/food">Food</NavLink></li>
      <li><NavLink to="/signup">SignUp</NavLink></li>
    </ul>
  </nav> 
  )
}



const Navbar = (props) => {
  const[isMobile,setIsMobile]=useState(
    window.matchMedia("(max-width:768px)").matches
  );

  useEffect(() =>{
    window.addEventListener("resize",()=>{
      setIsMobile(window.matchMedia("(max-width:768)").matches);
    });
  }) ;

const MobileList = () =>{
  const[active, setActive] = useState(false);
  return(
    <>
    <button onClick={() =>{
      setActive(!active);
    }}>
      {}Menu

    </button>
    </>
  )
}


  return(
    <div className="logo">
      <a className="heading-vertical">The</a>
      <a className="heading-bold " href="#">Siren</a>
      {isMobile ? <h1>mobile</h1> : <List/>}
    </div>
   )

 }


export default Navbar
